from typing import List


class Solution:
    def threeSum(self, nums: List[int]) -> List[List[int]]:
        nums.sort()
        i: int = 0
        sums: list[list[int]] = []
        while i < len(nums) - 2:

            # skip duplicate triplets.
            if i > 0 and nums[i] == nums[i - 1]:
                i += 1
                continue

            # when first element is greater than 0, break -> no possible way to get sum of 0 when sorted in ascending order
            if nums[i] > 0:
                break

            l: int = i + 1
            r: int = len(nums) - 1
            while l < r:
                if nums[i] + nums[l] + nums[r] > 0:
                    r -= 1
                elif nums[i] + nums[l] + nums[r] < 0:
                    l += 1
                else:
                    s = [nums[i], nums[l], nums[r]]
                    sums.append(s)

                    # skip duplicate triplets.
                    left_val, right_val = nums[l], nums[r]
                    while l < r and nums[l] == left_val:
                        l += 1
                    while l < r and nums[r] == right_val:
                        r -= 1

            i += 1

        return sums


